// src/components/task1.js

import React from 'react';


const data = [
  { Year: 2000, Population: 282200000 },
  { Year: 2001, Population: 284700000 },
  { Year: 2002, Population: 287300000 },

];


const DataRow = ({ Year, Population }) => (
  <tr>
    <td>{Year}</td>
    <td>{Population.toLocaleString()}</td>
  </tr>
);


const DataTable = ({ data }) => (
  <table border="1">
    <thead>
      <tr>
        <th>Year</th>
        <th>Population</th>
      </tr>
    </thead>
    <tbody>
      {data.map((item, index) => (
        <DataRow key={index} {...item} />
      ))}
    </tbody>
  </table>
);


const Task1 = () => (
  <div>
    <h1>Population Data</h1>
    <DataTable data={data} />
  </div>
);

export default Task1;
