import React, { useState } from 'react';
import { TimerContainer } from './task3.4';  // TimerContainer
import Timer from './task3.1';  // Timer
import Watch from './task3.5';  // Watch

const TimerControl = () => {
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [initialSeconds, setInitialSeconds] = useState(null);
  const [selectedRender, setSelectedRender] = useState(Timer);

  const handleStart = () => {
    const totalSeconds = (parseInt(hours, 10) * 3600) + (parseInt(minutes, 10) * 60) + parseInt(seconds, 10);
    setInitialSeconds(totalSeconds);
  };

  const handleInputChange = (e, setter) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setter(value);
    }
  };

  const handleRenderChange = (e) => {
    const renderType = e.target.value;
    setSelectedRender(renderType === 'watch' ? Watch : Timer);
  };

  return (
    <div>
      <div>
        <label>
          Hours:
          <input
            type="text"
            value={hours}
            onChange={(e) => handleInputChange(e, setHours)}
            style={{ width: '50px' }}
          />
        </label>
      </div>
      <div>
        <label>
          Minutes:
          <input
            type="text"
            value={minutes}
            onChange={(e) => handleInputChange(e, setMinutes)}
            style={{ width: '50px' }}
          />
        </label>
      </div>
      <div>
        <label>
          Seconds:
          <input
            type="text"
            value={seconds}
            onChange={(e) => handleInputChange(e, setSeconds)}
            style={{ width: '50px' }}
          />
        </label>
      </div>
      <div>
        <label>
          Render as:
          <select onChange={handleRenderChange}>
            <option value="timer">Timer</option>
            <option value="watch">Watch</option>
          </select>
        </label>
      </div>
      <button onClick={handleStart}>Start</button>
      {initialSeconds !== null && <TimerContainer seconds={initialSeconds} refresh={1000} render={selectedRender} />}
    </div>
  );
};

export default TimerControl;
